mapper.select:

SELECT *
  FROM my_contents
 WHERE contents_id = #{contents_id}
   AND language_id = #{language_id}


SELECT name
  FROM dr_customer_group_description
 WHERE customer_group_id = #{customer_group_id}
   AND language_id = #{language_id}


SELECT *
  FROM dr_customer_join_path
 WHERE join_path_id = #{join_path_id}
   AND language_id = #{language_id}
